INSERT INTO public.clinical_notes (patient_id, note_type, status, content, created_at) VALUES
('2887cfc2-9a39-49e2-b838-01c1e0463b51', 'progress', 'signed', '{"subjective":"Patient reports persistent fever, chills, and malaise. Complains of diffuse abdominal pain. Denies chest pain or dyspnea.","objective":"T 39.1°C, HR 112, BP 88/52 on levophed 0.08 mcg/kg/min, RR 22, SpO2 94% on 4L NC. WBC 18.2, Lactate 4.1 → 2.8 after 2L NS bolus. Blood cultures x2 drawn. CT abdomen shows possible cholangitis. Procalcitonin 12.4.","assessment":"Severe sepsis secondary to acute cholangitis. Hemodynamically unstable requiring vasopressor support. Lactate trending down with fluid resuscitation.","plan":"1. Continue norepinephrine, target MAP >65\n2. Meropenem 1g IV q8h (day 2)\n3. GI consult for possible ERCP\n4. Repeat lactate q4h\n5. Central line placement for vasopressor management\n6. Strict I/O monitoring"}', now() - interval '1 day'),
('2887cfc2-9a39-49e2-b838-01c1e0463b51', 'progress', 'draft', '{"subjective":"Patient slightly more alert this morning. Still febrile but reports less abdominal pain. Tolerating clear liquids.","objective":"T 38.4°C (trending down), HR 98, BP 102/64 (weaned off vasopressors 0400), RR 18, SpO2 96% on 2L NC. WBC 14.1 (↓ from 18.2). Lactate 1.8 (normalized). Blood cultures: Gram-negative rods in 2/2 bottles.","assessment":"Sepsis improving. Successfully weaned off vasopressors. Culture data pending speciation. Cholangitis as likely source.","plan":"1. Continue meropenem, narrow based on sensitivities\n2. ERCP scheduled for tomorrow AM\n3. Advance diet as tolerated\n4. Remove arterial line if stable x12h\n5. PT/OT evaluation for mobilization"}', now()),
('2887cfc2-9a39-49e2-b838-01c1e0463b51', 'consult', 'signed', '{"subjective":"GI consult requested for cholangitis management and ERCP planning.","objective":"CT abdomen: dilated common bile duct to 12mm with intrahepatic ductal dilation. No discrete mass. Gallbladder distended with pericholecystic fluid. Labs: Total bilirubin 4.2, direct 3.1, ALP 342, GGT 285.","assessment":"Acute cholangitis with biliary obstruction. Likely choledocholithiasis given absence of mass on CT. Tokyo criteria: Grade III (organ dysfunction).","plan":"1. ERCP with sphincterotomy and stone extraction scheduled\n2. If ERCP fails, percutaneous transhepatic biliary drainage\n3. Cholecystectomy after acute episode resolves\n4. Continue broad-spectrum antibiotics"}', now() - interval '12 hours');

